import React, { useContext, useEffect } from 'react'
import noteContext from '../context/notes/noteContext'
const About = () => {
  const a = useContext(noteContext)
  useEffect(() => {
    a.update();
  }, [])

  return (
    <div>
      this is About {a.name}
    </div>
  )
}

export default About
